package org.tnsif.stringsprogram;

public class StringOperationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1=new String("tushar");
		System.out.println(str1.toUpperCase());
		System.out.println(str1.length());
		System.out.println(str1.lastIndexOf('u'));
		System.out.println(str1.hashCode());
		System.out.println(str1.concat("sawant"));
		System.out.println(str1+"sawant");
	}

}
