package org.tnsif.userinputdemo;
import java.util.*;
public class ArthmaticOperatorProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.println("Enter the value of x and y:");
		int x=sc.nextInt();
		int y=sc.nextInt();
		System.out.println("the addition of two number:"+(x+y));
		System.out.println("the sub of two number:"+(x-y));
		System.out.println("the mul of two number:"+(x*y));
		System.out.println("the div of two number:"+(x/y));
	}

}
